//Select all divs and give them a purple background
$("div").css("background", "purple");

//select all divs with class highlight and make them 200px wide
$("div.highlight").css("width", "200px");

//select the div with id "third" and gibe it a organge border
$("div#third").css("border", "5px solid orange");

//select the first div only and change font color to pink
$("div:first-of-type").css("color", "pink");